import axios from "axios";
import React from "react";
import { Icon } from "semantic-ui-react";
import { apiBaseUrl } from "../constants";
import DiagnosisList from "../PatientListPage/DiagnosesList";
import { setDiagnosisList, useStateValue } from "../state";
import { Diagnosis, Entry } from "../types";

const EntryDetails = ({ entry } : { entry: Entry }): JSX.Element => {
  const [{ diagnoses }, dispatch] = useStateValue();
  
  if (diagnoses.length === 0) {
    React.useEffect(() => {
      const fetchDiagnosisCodes = async () => {
        try {
          const { data: diagnosesFromApi } = await axios.get<Diagnosis[]>(
            `${apiBaseUrl}/diagnoses`
          );
  
          dispatch(setDiagnosisList(diagnosesFromApi));
        } catch (e) {
          console.error(e);
        }
      };
      void fetchDiagnosisCodes();
    }, [dispatch]);
  }
  
  return (
    <div key={entry.id}>
          {entry.date}: {entry.description}
          {!entry.diagnosisCodes
            ? null
            : !diagnoses 
              ?  <Icon loading name='spinner' size='big' /> 
              :  <DiagnosisList diagnoses={diagnoses} codes={entry.diagnosisCodes} />
          }
    </div>
  );
};

export default EntryDetails;
